import * as actionTypes from '../actions';

import operate from "../../components/calculator/Operate"

const initialState = {
    total: '',
    operation: '',
    next: ''
};

const reducer = (state = initialState, action: any) => {
    switch (action.type) {
        case actionTypes.NUMBERS:
            if (action.payload.buttonName === 'AC') {
                return {
                    ...state,
                    total: null,
                    operation: null,
                    next: null
                };
            }

            if (Number(action.payload.buttonName)) {
                if (action.payload.buttonName === "0" && state.next === "0") {
                    return {
                        ...state,
                        total: null,
                        operation: null,
                        next: null
                    };
                }
                if (state.operation) {
                    if (state.next) {
                        return {
                            ...state,
                            next: state.next + action.payload.buttonName
                        };
                    }
                    return {
                        ...state,
                        next: action.payload.buttonName
                    };
                }
                if (state.next) {
                    return {
                        ...state,
                        next: state.next + action.payload.buttonName,
                        total: null,
                    };
                }
                return {
                    ...state,
                    next: action.payload.buttonName,
                    total: null,
                };
            }

            if (action.payload.buttonName === '+') {

                return {
                    ...state,
                    total: operate(1, 2),
                    next: null,
                    operation: action.payload.buttonName,
                };
            }
    }
    return state
};

export default reducer
