import * as actionTypes from '../actions';

const initialState = {
    total: null,
    operation: null,
    next: null
};

const reducer = (state = initialState, action: any) => {
    const clickedButtonName = action.payload.buttonName

    switch (action.type) {
        case actionTypes.NUMBERS:
            if (Number(clickedButtonName)) {
                if (clickedButtonName === "0" && state.next === "0") {
                    return {};
                }
                // If there is an operation, update next
                if (state.operation) {
                    if (state.next) {
                        return { next: state.next + clickedButtonName };
                    }
                    return { next: clickedButtonName };
                }
                // If there is no operation, update next and clear the value
                if (state.next) {
                    return {
                        next: state.next + clickedButtonName,
                        total: null,
                    };
                }
                return {
                    next: clickedButtonName,
                    total: null,
                };
            }
    }
    return state
};

export default reducer
